import React, { useEffect } from 'react';
import {
  View, Text, StatusBar, StyleSheet, Dimensions, Image, TouchableOpacity,
} from 'react-native';
import { connect } from 'react-redux';
import { AnyAction, bindActionCreators, Dispatch } from 'redux';
import { ScrollView } from 'react-native-gesture-handler';
import { getRecipeFromAPI, getRecipeByNameFromAPI } from '../../redux/actions/recipesActions';

const styles = StyleSheet.create({
  header: {
    position: 'absolute',
    top: 0,
    zIndex: 1,
    width: '100%',
    height: 50,
    backgroundColor: 'rgb(230, 84, 84)',
    paddingHorizontal: 10,
    justifyContent: 'center',
  },
});

interface Recipe {
    strMealThumb: string
    strMeal: string
}

interface Navigation {
    navigate(route: string, data: object): void
}

function Category({
  recipes, categoryRecipesByNameReducer, actions, navigation, route: { params: { categoryName } },
}
    : { recipes: Array<Recipe>, actions: object, navigation: Navigation,
        route: { params: { categoryName: string } } }) {
  const { height } = Dimensions.get('window');
  const recipePhotoHeight = +(height - 114).toFixed();
  let categoryRecipeIndex;
  console.log(categoryRecipesByNameReducer);

  let recipePhotoUri: string;
  let recipeName;
  const getRecipeNameAndPhoto = (index: number) => {
    recipePhotoUri = recipes[index].meals[0].strMealThumb;
    recipeName = recipes[index].meals[0].strMeal;
  };

  const recipesJSX: JSX.Element[] = [];
  const addRecipeToScrollView = (index: number) => {
    getRecipeNameAndPhoto(index);
    recipesJSX.push(
      <TouchableOpacity
        key={Math.random() * Date.now()}
        onPress={() => {
          console.log('tbs');
          navigation.navigate('detail', {
            recipe: recipes[index].meals[0],
          });
        }}
        activeOpacity={1}
      >
        <Image
          style={{ height: recipePhotoHeight, position: 'relative', top: 50 }}
          source={{ uri: recipePhotoUri }}
        />
      </TouchableOpacity>,
    );
  };

  if (recipes.length) {
    recipes.forEach((element: object, index: number) => {
      addRecipeToScrollView(index);
    });
  }

  useEffect(() => {
    if (Object.keys(recipes).length) {
      for (categoryRecipeIndex = 0; categoryRecipeIndex < 3; categoryRecipeIndex += 1) {
        console.log('entrooo');
        actions.getRecipeByNameFromAPI(recipes.meals[categoryRecipeIndex].strMeal, true);
      }
    }
  }, []);

  let scrollViewContentOffsetY = 0;
  let goingDown : boolean;

  return (
    <View style={{ marginTop: StatusBar.currentHeight }}>
      <StatusBar backgroundColor="black" barStyle="light-content" translucent />
      <View style={styles.header}>
        <Text style={{ color: 'white', fontSize: 25 }}>
          {`${categoryName} category`}
        </Text>
      </View>
      <ScrollView
        pagingEnabled
        decelerationRate={0}
        snapToInterval={recipePhotoHeight}
        snapToAlignment="center"
        showsVerticalScrollIndicator={false}
        onScroll={(event) => {
          if (event.nativeEvent.contentOffset.y > scrollViewContentOffsetY) {
            goingDown = true;
          } else {
            goingDown = false;
          }
          scrollViewContentOffsetY = event.nativeEvent.contentOffset.y;
        }}
        onScrollEndDrag={() => {
          if (goingDown) {
            actions.getRecipeFromAPI();
          }
        }}
      >
        {recipesJSX}
      </ScrollView>
    </View>
  );
}

function mapStateToProps({ categoryRecipesReducer, categoryRecipesByNameReducer }
    : { categoryRecipesReducer: object}) {
  return {
    recipes: categoryRecipesReducer,
    categoryRecipes: categoryRecipesByNameReducer,
  };
}

function mapDispatchToProps(dispatch: Dispatch<AnyAction>) {
  return {
    actions: bindActionCreators({ getRecipeFromAPI, getRecipeByNameFromAPI }, dispatch),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Category);
