export default function searchReducer(state = {}, action: { type: String; recipe: Object; }) {
  switch (action.type) {
    case 'GET_RECIPE_BY_NAME':
      return action.recipe.meals[0];
    case 'RESTORE_SEARCH_REDUCER':
      return {};
    default:
      return state;
  }
}
