import React, { useRef, useEffect } from 'react';
import {
  StyleSheet, View, Text, StatusBar, Image, ScrollView,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Icon } from 'react-native-elements';
import { connect } from 'react-redux';
import { AnyAction, bindActionCreators, Dispatch } from 'redux';
import PropTypes from 'prop-types';
import {
  getRecipeByNameFromAPI, restoreSearchReducer,
  getCategoryRecipesFromAPI, restoreCategoryRecipeByNameReducer,
} from '../../redux/actions/recipesActions';
import SearchBoxInput from './TextInput';

const styles = StyleSheet.create({
  header: {
    position: 'relative',
    top: 0,
    zIndex: 1,
    width: '100%',
    backgroundColor: 'rgb(230, 84, 84)',
    paddingHorizontal: 10,
    justifyContent: 'center',
  },
});

interface Recipe {
  strMealThumb?: string
  strMeal?: string
}

interface Category {
  strCategory: string
}

interface Categories {
  categories: Array<Category>
}

interface Navigation {
  navigate(route: string, data: object): void
}

interface Actions {
  restoreSearchReducer(): void
  getRecipeByNameFromAPI(text : string): void
  getCategoryRecipesFromAPI(text : string): void
}

function Search({
  recipe, categories, actions, navigation,
} : { recipe: Recipe, categories: Categories, actions: Actions, navigation: Navigation}) {
  const searchBoxRef = useRef();

  useEffect(() => {
    if (Object.keys(recipe).length) {
      navigation.navigate('detail', {
        recipe,
      });
      actions.restoreSearchReducer();
    }
  }, [Object.keys(recipe).length]);

  useEffect(() => {
    actions.restoreCategoryRecipeByNameReducer();
  });

  return (
    <View style={{ marginTop: StatusBar.currentHeight }}>
      <StatusBar backgroundColor="black" barStyle="light-content" translucent />
      <View style={styles.header}>
        <Text style={{ color: 'white', fontSize: 25, paddingVertical: 10 }}>
          Search
        </Text>
        <View style={{
          flexDirection: 'row',
          alignItems: 'center',
          backgroundColor: 'white',
          borderRadius: 5,
          paddingHorizontal: 10,
          marginBottom: 10,
        }}
        >
          <Icon size={30} name="ios-search" type="ionicon" onPress={() => searchBoxRef.current.focus()} />
          <SearchBoxInput searchBoxRef={searchBoxRef} />
        </View>
      </View>
      <ScrollView>
        <View
          onTouchStart={() => searchBoxRef.current.blur()}
          style={{
            flexDirection: 'row', flexWrap: 'wrap', paddingHorizontal: 5, marginBottom: 155,
          }}
        >
          {categories.categories.map((category : object) => {
            if (category.strCategory !== '') {
              return (
                <View
                  key={Math.random() * Date.now()}
                  style={{
                    width: '50%',
                    alignItems: 'center',
                    shadowColor: '#000',
                    shadowOpacity: 1,
                  }}
                  onTouchEnd={() => {
                    actions.getCategoryRecipesFromAPI(category.strCategory);
                    navigation.navigate('category', {
                      categoryName: category.strCategory,
                    });
                  }}
                >
                  <LinearGradient
                    start={{ x: 0, y: 1 }}
                    end={{ x: 1, y: 0 }}
                    colors={['rgb(236, 154, 60)', 'rgb(235, 157, 69)']}
                    style={{
                      elevation: 10,
                      width: 180,
                      height: 105,
                      marginTop: 13,
                      borderRadius: 15,
                      justifyContent: 'center',
                      alignItems: 'center',
                    }}
                  >
                    <Text style={{
                      fontSize: 20,
                      position: 'relative',
                      top: 15,
                      color: 'rgb(44, 42, 40)',
                    }}
                    >
                      {category.strCategory.toUpperCase()}
                    </Text>
                    <Image
                      style={{
                        width: 90, height: 90, borderRadius: 45, resizeMode: 'contain', position: 'relative', top: 1,
                      }}
                      source={{ uri: category.strCategoryThumb }}
                    />
                  </LinearGradient>
                </View>
              );
            }
          })}
        </View>
      </ScrollView>
    </View>
  );
}

function mapStateToProps({ searchReducer, recipeCategoriesReducer }
  : { searchReducer: object, recipeCategoriesReducer: object}) {
  return {
    recipe: searchReducer,
    categories: recipeCategoriesReducer,
  };
}

function mapDispatchToProps(dispatch: Dispatch<AnyAction>) {
  return {
    actions: bindActionCreators({
      getRecipeByNameFromAPI,
      restoreSearchReducer,
      getCategoryRecipesFromAPI,
      restoreCategoryRecipeByNameReducer,
    }, dispatch),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Search);
