import React, { useEffect } from 'react';
import {
  Dimensions, Text, ScrollView, View, StatusBar, Image,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import PropTypes from 'prop-types';
import DeviceInfo from 'react-native-device-info';
import { getRecipeFromAPI } from '../../redux/actions/recipesActions';
// import Constants from 'expo-constants';
/* statusBarHeight */

function Detail({ recipe, actions } : { recipe: Object, actions:Object}) {
  DeviceInfo.getIpAddress().then((ip) => {
    console.log(ip);
  });
  console.log('hola');
  const recipeReducerStateLength = Object.keys(recipe).length;

  let uri;
  let recipeName;

  if (recipeReducerStateLength) {
    uri = recipe.meals[0].strMealThumb;
    recipeName = recipe.meals[0].strMeal;
  }

  useEffect(() => {
    if (!recipeReducerStateLength) {
      actions.getRecipeFromAPI();
    }
  }, [recipeReducerStateLength]);

  const { height } = Dimensions.get('window');
  const whiteBoxHeight = Dimensions.get('window').height - 280;

  return (

    <View>
      <StatusBar backgroundColor="black" barStyle="light-content" translucent />
      <ScrollView showsVerticalScrollIndicator={false}>
        <View>
          <Image style={{ height: height - 40 }} source={{ uri }} />
          <LinearGradient
            colors={['transparent', 'rgb(250, 250, 250)']}
            style={{
              position: 'absolute', top: whiteBoxHeight, backgroundColor: 'linear-gradient(45deg, rgba(250, 250, 250, 0.4), rgba(250, 250, 250, 0))', width: '100%', height: 240, alignItems: 'center', paddingTop: 10,
            }}
          >
            <Text style={{
              color: 'black',
              fontSize: 30,
              fontWeight: 'bold',
              textShadowColor: 'rgba(250, 250, 250, 0.5)',
              textShadowOffset: { width: -1, height: 1 },
              textShadowRadius: 10,
            }}
            >
              {recipeName}
            </Text>
          </LinearGradient>
        </View>
        <View>
          <Text>M</Text>
          <Text>M</Text>
          <Text>M</Text>
          <Text>M</Text>
          <Text>M</Text>
          <Text>M</Text>
          <Text>M</Text>
        </View>
      </ScrollView>
      <View
        style={{
          position: 'absolute', top: 0, zIndex: 1, marginTop: StatusBar.currentHeight, width: '100%', backgroundColor: 'rgb(230, 84, 84)', paddingHorizontal: 10,
        }}
      >
        <Ionicons
          size={40}
          style={{
            color: 'white',
          }}
          name="ios-arrow-round-back"
          type="ionicons"
        />
      </View>
    </View>
  );
}

function mapStateToProps({ recipesReducer } : { recipesReducer: Object}) {
  return {
    recipe: recipesReducer,
  };
}

function mapDispatchToProps(dispatch: any) {
  return {
    actions: bindActionCreators({ getRecipeFromAPI }, dispatch),
  };
}

Detail.propTypes = {
  recipe: PropTypes.shape({
    meals: PropTypes.arrayOf(PropTypes.object),
  }).isRequired,
  actions: PropTypes.shape({
    getRecipeFromAPI: PropTypes.func.isRequired,
  }).isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(Detail);
