import React, { useState, useRef } from 'react';
import {
  StyleSheet, View, Text, StatusBar, TextInput, TouchableWithoutFeedback, Keyboard,
} from 'react-native';
import { Icon } from 'react-native-elements';

const styles = StyleSheet.create({
  header: {
    position: 'absolute',
    top: 0,
    zIndex: 1,
    width: '100%',
    backgroundColor: 'rgb(230, 84, 84)',
    paddingHorizontal: 10,
    justifyContent: 'center',
  },
});

export default function Search(): any {
  const [text, setText] = useState('');
  const inputRef = useRef(null);
  return (
    <View style={{ marginTop: StatusBar.currentHeight }}>
      <StatusBar backgroundColor="black" barStyle="light-content" translucent />
      <View style={styles.header}>
        <Text style={{ color: 'white', fontSize: 25, paddingVertical: 10 }}>
          Search
        </Text>
        <View style={{
          flexDirection: 'row',
          alignItems: 'center',
          backgroundColor: 'white',
          borderRadius: 5,
          paddingHorizontal: 10,
          marginBottom: 10,
        }}
        >
          <Icon size={30} name="ios-search" type="ionicon" />
          {/* <TouchableWithoutFeedback style={{ flex: 1 }} onPress={Keyboard.dismiss} accessible={false}>

            <TextInput
              style={{ height: 40, fontSize: 17, marginLeft: 10 }}
              placeholder="Type a recipe name here!"
