import React, { useEffect } from 'react';
import {
  View, Text, StatusBar, StyleSheet, Dimensions, Image,
} from 'react-native';
import { connect } from 'react-redux';
import { AnyAction, bindActionCreators, Dispatch } from 'redux';
import PropTypes from 'prop-types';
import { ScrollView } from 'react-native-gesture-handler';
import { getRecipeFromAPI } from '../../redux/actions/recipesActions';

const styles = StyleSheet.create({
  header: {
    position: 'absolute',
    top: 0,
    zIndex: 1,
    width: '100%',
    height: 50,
    backgroundColor: 'rgb(230, 84, 84)',
    paddingHorizontal: 10,
    justifyContent: 'center',
  },
});

function Home({ recipes, actions } : { recipes: Object, actions: Object}) {
  const { height } = Dimensions.get('window');

  let recipePhotoUri: string;
  let recipeName;
  const getRecipeNameAndPhoto = () => {
    recipePhotoUri = recipes[0].strMealThumb;
    recipeName = recipes[0].strMeal;
  };

  const recipesJSX: JSX.Element[] = [];
  const addRecipeToScrollView = () => {
    getRecipeNameAndPhoto();
    recipesJSX.push(
      <Image
        key={Math.random() * Date.now()}
        style={{ height: height - 40 }}
        source={{ uri: recipePhotoUri }}
      />,
    );
  };

  if (recipes.length) {
    recipes.forEach(() => addRecipeToScrollView());
  }

  useEffect(() => {
    if (!recipes.length) {
      actions.getRecipeFromAPI();
      actions.getRecipeFromAPI();
      actions.getRecipeFromAPI();
    }
  }, []);
  return (
    <View>
      <StatusBar backgroundColor="black" barStyle="light-content" />
      <View style={styles.header}>
        <Text style={{ color: 'white', fontSize: 25 }}>
          Recipes
        </Text>
      </View>
      <ScrollView>
        {recipesJSX}
      </ScrollView>
    </View>
  );
}

function mapStateToProps({ recipesReducer } : { recipesReducer: Object}) {
  debugger;
  return {
    recipes: recipesReducer,
  };
}

function mapDispatchToProps(dispatch: Dispatch<AnyAction>) {
  return {
    actions: bindActionCreators({ getRecipeFromAPI }, dispatch),
  };
}

Home.propTypes = {
  recipes: PropTypes.arrayOf(PropTypes.object).isRequired,
  actions: PropTypes.shape({
    getRecipeFromAPI: PropTypes.func.isRequired,
  }).isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
