import React, { useState } from 'react';
import { TextInput } from 'react-native';
import { connect } from 'react-redux';
import { AnyAction, bindActionCreators, Dispatch } from 'redux';
import PropTypes from 'prop-types';
import { getRecipeByNameFromAPI } from '../../redux/actions/recipesActions';

interface Actions {
    getRecipeByNameFromAPI(text : string, boolean: boolean) : void
}

interface SearchBoxRef {
    (): void
}

function SearchBoxInput({ searchBoxRef, actions }
    : {searchBoxRef: SearchBoxRef, actions: Actions}) {
  const [text, setText] = useState<string>('');

  return (
    <TextInput
      style={{
        height: 40, fontSize: 17, marginLeft: 10, width: '100%',
      }}
      placeholder="Type a recipe here!"
      onChangeText={(textChange) => setText(textChange)}
      defaultValue={text}
      ref={searchBoxRef}
      onSubmitEditing={() => actions.getRecipeByNameFromAPI(text, false)}
    />
  );
}

function mapDispatchToProps(dispatch: Dispatch<AnyAction>) {
  return {
    actions: bindActionCreators({ getRecipeByNameFromAPI }, dispatch),
  };
}

export default connect(null, mapDispatchToProps)(SearchBoxInput);
