import React, { useEffect } from 'react';
import {
  Dimensions, Text, ScrollView, View, StatusBar, Image,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import PropTypes from 'prop-types';
import { getRecipeFromAPI } from '../../redux/actions/recipeActions';
// import Constants from 'expo-constants';
/* statusBarHeight */

function Detail({ recipe, actions } : { recipe:Object, actions:Object}) {
  const recipeReducerStateLength = Object.keys(recipe).length;
  console.log(recipe);
  console.log('hola');
  console.log(recipeReducerStateLength);

  useEffect(() => {
    if (!recipeReducerStateLength) {
      actions.getRecipeFromAPI();
    }
  }, [recipeReducerStateLength]);

  const { height } = Dimensions.get('window');
  const whiteBoxHeight = Dimensions.get('window').height - 200;
  return (
    <View>
      <StatusBar backgroundColor="black" barStyle="light-content" translucent />
      <ScrollView showsVerticalScrollIndicator={false}>
        <View>
          <Image style={{ height }} source={{ uri: 'https://images.immediate.co.uk/production/volatile/sites/30/2020/08/chorizo-mozarella-gnocchi-bake-cropped-9ab73a3.jpg?quality=90&resize=960,872' }} />
          <View style={{
            position: 'absolute', top: whiteBoxHeight, backgroundColor: 'white', width: '100%', height: 200,
          }}
          >
            <Text>holaaae</Text>
          </View>
        </View>
        <View>
          <Text>Adioooos</Text>

        </View>
      </ScrollView>
      <Ionicons
        size={40}
        style={{
          marginTop: StatusBar.currentHeight, color: 'white', position: 'absolute', top: 0, zIndex: 1,
        }}
        name="ios-arrow-round-back"
      />
    </View>
  );
}

function mapStateToProps({ recipesReducer } : { recipesReducer: Object}) {
  return {
    recipe: recipesReducer,
  };
}

function mapDispatchToProps(dispatch: any) {
  return {
    actions: bindActionCreators({ getRecipeFromAPI }, dispatch),
  };
}

Detail.propTypes = {
  recipe: PropTypes.shape({
  }).isRequired,
  actions: PropTypes.shape({
    getRecipeFromAPI: PropTypes.func.isRequired,
  }).isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(Detail);
